import React from "react";
import { useMediaQuery } from "react-responsive";
import "../../App.css";

export default function ContactUs() {
  const isDesktopOrLaptop = useMediaQuery({
    query: "(min-device-width: 1224px)",
  });
  const isBigScreen = useMediaQuery({ query: "(min-device-width: 1824px)" });
  const isTabletOrMobile = useMediaQuery({ query: "(max-width: 1224px)" });
  const isTabletOrMobileDevice = useMediaQuery({
    query: "(max-device-width: 1224px)",
  });
  const isPortrait = useMediaQuery({ query: "(orientation: portrait)" });
  const isRetina = useMediaQuery({ query: "(min-resolution: 2dppx)" });
  return (
    <div>
      <div className="contact">
        <h4>İLETİŞİM</h4>
        <p>Firmamıza ait iletişim bilgilerine bu sayfadan ulaşabilirsiniz. </p>
      </div>
      <div>
        {/* <iframe
          title="harita"
          src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d24459.638632400893!2d32.739152!3d39.97594!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x2e7d61e0cccb27bc!2sSY%20Makine!5e0!3m2!1str!2str!4v1618561344251!5m2!1str!2str"
          width="600"
          height="450"
          allowfullscreen=""
          loading="lazy"
          style={{ border: "0" }}
        ></iframe> */}
        <div>
          <h1>Device Test!</h1>
          {isDesktopOrLaptop && (
            <>
              <p>You are a desktop or laptop</p>
              {isBigScreen && <p>You also have a huge screen</p>}
              {isTabletOrMobile && (
                <p>You are sized like a tablet or mobile phone though</p>
              )}
            </>
          )}
          {isTabletOrMobileDevice && <p>You are a tablet or mobile phone</p>}
          <p>Your are in {isPortrait ? "portrait" : "landscape"} orientation</p>
          {isRetina && <p>You are retina</p>}
        </div>

        <p>iletişim bilgileri</p>
        <p>map</p>
        <p>iletişim forumu</p>
      </div>
    </div>
  );
}
